     static class NumberGenerator{

         private static volatile boolean printEvenNumber = false;


         public  void printEvenNumber(int i) {
             synchronized (this) {
                 if(!printEvenNumber) {
                     try {
                         wait();
                     } catch (InterruptedException e) {
                         e.printStackTrace();
                     }
                 }
                 System.out.println(i);
                 printEvenNumber = !printEvenNumber;
                 notify();
