							try {
								obj.wait();
							} catch (InterruptedException e) {
								// TODO Auto-generated catch block
								e.printStackTrace();
							}
						}
						System.out.println(&quot;Odd:&quot;+i);
						oddFlag = false;
						evenFlag = true;
						obj.notify();
					}
				}
			}
		});
		
		Thread even = new Thread(new Runnable() {
			@Override
			public void run() {
				for(int i = 2; i &lt;= upto; i = i + 2){
