        System.out.println(&quot;Encrypted Data ===&gt; &quot;+encryptData(inputStr, shiftKey));   
        System.out.println(&quot;Decrypted Data ===&gt; &quot;+decryptData(encryptData(inputStr, shiftKey), shiftKey));   
          
        // close Scanner class object   
        sc.close();   
    }   
}

