                &lt;/classpath&gt;
                &lt;arg value=&quot;-xmlschema&quot;/&gt;
                &lt;arg value=&quot;-encoding&quot;/&gt;
                &lt;arg value=&quot;UTF-8&quot;/&gt;
                &lt;schema file=&quot;xml-resources/jaxb/mypackage/mypackage.xsd&quot;/&gt;
                &lt;depends file=&quot;xml-resources/jaxb/mypackage/mypackage.xsd&quot;/&gt;
                &lt;produces dir=&quot;build/generated/jaxbCache/mypackage&quot;/&gt;
