          return left_helper(arr, target, abs_diff, lo, max(mid - 1, lo))


      def right_helper(arr, target, abs_diff, lo, hi):
        # Base case
        if lo == hi:
          diff = arr[lo] - target
          if abs(diff) == abs_diff:
