binarysearch(int low,int high,int val)
{
    if(val&lt;=arr[0])
        return 0;
    if(val&gt;=arr[n-1])
        return arr[n-1];
    while(low&lt;=high)
    {
        int mid=(low+high)/2;
        if(v[mid]&gt;val)
            return binarysearch(low,mid-1,val);
        else if(v[mid]&lt;val)
            return binarysearch(mid+1,high,val);
        else return mid;
    }
    if(abs(val-v[low])&gt;=abs(val-v[high]))
