    public class Hello {
    	
    	public static void hello(){
    		try{
    			System.out.println(&quot;hi&quot;);
    			return;
    			}catch(RuntimeException e){
    		}finally{
    			System.out.println(&quot;finally&quot;);
    			
    		}
    
    	}
    	
    	public static void main(String[] args){
