~680fps

    import java.awt.Canvas;
    import java.awt.Color;
    import java.awt.Dimension;
    import java.awt.FontMetrics;
    import java.awt.Frame;
    import java.awt.Graphics2D;
    import java.awt.image.BufferStrategy;
    import java.time.Duration;
    import java.time.Instant;
    import java.util.concurrent.atomic.AtomicBoolean;

    public class Other {
        public static void main(String[] args) {
            new Other();
        }

        public Other() {
            Frame frame = new Frame();
            MainCanvas canvas = new MainCanvas();
            frame.setBackground(Color.BLUE);
            frame.add(canvas);
            frame.setLocationRelativeTo(null);
            frame.pack();
            frame.setVisible(true);
        }

        public class MainCanvas extends Canvas {

            private Thread thread;
            private AtomicBoolean running = new AtomicBoolean(false);
            private int fps;

            public MainCanvas() {
            }

            @Override
            public void addNotify() {
                super.addNotify();
                createBufferStrategy(3);
                start();
            }

            @Override
            public void removeNotify() {
                stop();
                super.removeNotify();
            }

            public void start() {
                stop();
                run();
            }

            protected void stop() {
                if (running.get()) {
                    running.set(false);
                }
                thread = null;
            }

            protected void run() {
                stop();

                running.set(true);
                thread = new Thread(new Runnable() {
                    private Instant lastTick;
                    private int frameCount;

                    @Override
                    public void run() {
                        while (running.get()) {
                            if (lastTick != null) {
                                Duration duration = Duration.between(lastTick, Instant.now());
                                if (duration.toMillis() &gt;= 1000) {
                                    lastTick = Instant.now();
                                    fps = frameCount;
                                    frameCount = 0;
                                } else {
                                    frameCount++;
                                }
                            } else {
                                lastTick = Instant.now();
                            }
                            render();
                            // Comment out these lines and see where it takes you
                            try {
                                Thread.sleep(1);
                            } catch (InterruptedException ex) {
                                java.util.logging.Logger.getLogger(Other.class.getName()).log(java.util.logging.Level.SEVERE, null, ex);
                            }
                        }
                    }
                });
                thread.start();
            }

            protected void render() {
                BufferStrategy bs = getBufferStrategy();
                while (bs == null) {
                    bs = getBufferStrategy();
                }
                do {
                    // The following loop ensures that the contents of the drawing buffer
                    // are consistent in case the underlying surface was recreated
                    do {
                        // Get a new graphics context every time through the loop
                        // to make sure the strategy is validated
                        Graphics2D g2d = (Graphics2D) bs.getDrawGraphics();

                        g2d.setColor(Color.GREEN);
                        g2d.fillRect(0, 0, getWidth(), getHeight());
                        // Render to graphics
                        // ...
                        g2d.setColor(Color.RED);
                        g2d.fillOval(20, 20, 20, 20);
                        g2d.fillOval(40, 40, 20, 20);

                        FontMetrics fm = g2d.getFontMetrics();
                        String text = Integer.toString(fps);
                        g2d.drawString(text, 10, fm.getAscent());
                        // Dispose the graphics
                        g2d.dispose();

                        // Repeat the rendering if the drawing buffer contents
                        // were restored
                    } while (bs.contentsRestored());

                    // Display the buffer
                    bs.show();

                    // Repeat the rendering if the drawing buffer was lost
                } while (bs.contentsLost());
            }

            @Override
            public Dimension getPreferredSize() {
                return new Dimension(400, 400);
            }
        }
    }

