        &lt;artifactId&gt;maven-surefire-report-plugin&lt;/artifactId&gt;
        &lt;version&gt;${maven-surefire-plugin.version}&lt;/version&gt;
        &lt;configuration&gt;
          &lt;outputDirectory&gt;target/surefire-reports&lt;/outputDirectory&gt;
          &lt;linkXRef&gt;false&lt;/linkXRef&gt;
        &lt;/configuration&gt;
      &lt;/plugin&gt;
