       17:	aload_0
       18:	getfield	#6; //Field x:I
       21:	invokevirtual	#7; //Method java/lang/StringBuilder.append:(I)Ljava/lang/StringBuilder;
       24:	invokevirtual	#8; //Method java/lang/StringBuilder.toString:()Ljava/lang/String;
