    Cursor res = db.rawQuery(&quot; Select * from &quot; + TABLE_NAME + &quot; where EMAIL= &#39;&quot; + email + &quot; &#39;and PASSWORD= &#39;&quot; + password + &quot;&#39;&quot;, null);

