        Map&lt;String, String&gt; options = new LinkedHashMap&lt;&gt;();
        options.put(&quot;access&quot;, &quot;public&quot;);
        options.put(&quot;foo&quot;, &quot;bar&quot;);
    
        System.out.println(mapToJson(options));
