package com.nea.ecommerceBackend.service;

import com.nea.ecommerceBackend.api.model.LoginBody;
import com.nea.ecommerceBackend.api.model.RegistrationBody;
import com.nea.ecommerceBackend.exception.UserAlreadyExistException;
import com.nea.ecommerceBackend.model.LocalUser;
import com.nea.ecommerceBackend.model.dao.LocalUserDAO;

import org.springframework.stereotype.Service;

import java.util.Optional;

@Service
public class UserService {


    private LocalUserDAO localUserDAO;
    private EncryptionService encryptionService;

    private JWTService jwtService;

    public UserService(LocalUserDAO localUserDAO, EncryptionService encryptionService, JWTService jwtService) {
        this.localUserDAO = localUserDAO;
        this.encryptionService = encryptionService;
        this.jwtService = jwtService;
    }




    public LocalUser registerUser(RegistrationBody registrationBody) throws UserAlreadyExistException {
        if(localUserDAO.findByEmailIgnoreCase(registrationBody.getEmail()).isPresent()
                || localUserDAO.findByUsernameIgnoreCase(registrationBody.getUsername()).isPresent()){
            throw new UserAlreadyExistException();
        }

        LocalUser user = new LocalUser();
        user.setEmail(registrationBody.getEmail());
        user.setFirstname(registrationBody.getFirstName());
        user.setLastname((registrationBody.getLastName()));
        user.setUsername(registrationBody.getUsername());

        user.setPassword(encryptionService.encryptPassword(registrationBody.getPassword()));
        return localUserDAO.save(user);

    }

    public String loginUser(LoginBody loginBody) {
        Optional&lt;LocalUser&gt; opUser = localUserDAO.findByUsernameIgnoreCase(loginBody.getUsername());
        if (opUser.isPresent()) {
            LocalUser user = opUser.get();
            if (encryptionService.verifyPassword(loginBody.getPassword(), user.getPassword())) {
                return jwtService.generateJWT(user);
            }
        }
        return null;
    }

}
