Specification&lt;MyDomain&gt; querySpec = Specifications.where((root, query, cb) -&gt; {
     Predicate p = null;
     //iterate over the map
     mapParam.forEach(k,v -&gt; {
       if(k != null &amp;&amp; v!= null) {
