    public class AlterSuiteListener implements IAlterSuiteListener {

    @Override
    public void alter(List&lt;XmlSuite&gt; suites) {
        for (int i = 0; i &lt; suites.size(); i++) {
            XmlSuite xmlSuite = suites.get(0);
            List&lt;XmlTest&gt; tests = suites.get(i).getTests();
            int xmlTestSize = tests.size();
            List&lt;String&gt; browsers = Arrays.asList(System.getenv(&quot;Browser&quot;).split(&quot;,&quot;));
            List&lt;XmlTest&gt; expectedXmlTests = new ArrayList&lt;&gt;();
            for (int xmlTestIndex = 0; xmlTestIndex &lt; xmlTestSize; xmlTestIndex++) {
                XmlTest defaultXmlTest = tests.get(xmlTestIndex);
                for (String browser : browsers) {
                    XmlTest xmlTest = new XmlTest(xmlSuite);
                    xmlTest.setName(defaultXmlTest.getName() + &quot;_&quot; + browser);
                    xmlTest.setClasses(defaultXmlTest.getClasses());
                    xmlTest.setThreadCount(defaultXmlTest.getThreadCount());
                    xmlTest.setParallel(defaultXmlTest.getParallel());
                    Map&lt;String, String&gt; allParameters = defaultXmlTest.getAllParameters();
                    allParameters.put(&quot;Browser&quot;,browser);
                    xmlTest.setParameters(allParameters);
                    expectedXmlTests.add(xmlTest);
                }
            }
            xmlSuite.setTests(expectedXmlTests);
        }
    }
    }

