        for(int[] row : memo) {
            Arrays.fill(row, -1);
        }
        return move(nums, 0, 0, 0, memo);
    }

    private static int move(int[] nums, int i, int sum1, int sum2, int[][] memo) {
        if(i &gt;= nums.length) return Math.abs(sum1 - sum2); //if at the end, return difference
