    			//System.out.println();
    			range--;
    		}
    		if(coeff.size() == 1) {
    			return;
    		}
    		while(range&gt;=0) {
    			int remTotal = total - range*coeff.get(index);
    			Map&lt;Integer, Integer&gt; coeffCopy = new HashMap&lt;Integer, Integer&gt;(coeff);
    			coeffCopy.remove(index);
    			sol.put(index, range);
    			findPossibleSolutions(remTotal, coeffCopy);
    			range--;
    		}
    	}
    
