    	}
    
    	/**
    	 * @param coeff
    	 */
    	private int returnLargestIndex(Map&lt;Integer, Integer&gt; coeff) {
    		int largestKey = coeff.keySet().iterator().next();
    		for(int i : coeff.keySet()) {
    			if(coeff.get(i)&gt;coeff.get(largestKey)) {
