final int minValue = 1; // constraint: values are in range [1, 100]
        
for (int next: arr) {
    freq[next - minValue]++;
}
        
for (int i = 0; i &lt; freq.length; i++) {
    if (freq[i] &gt; 1) System.out.print((i + minValue + &quot; &quot;).repeat(freq[i]));
}
