public final class Gron {
    private Gron() {}

    public static List&lt;String&gt; paths(final Object obj) {
        return paths(&quot;&quot;, obj);
    }

    public static List&lt;String&gt; paths(final String path, final Object obj) {
        final List&lt;String&gt; paths = new ArrayList&lt;&gt;();

        if (obj instanceof String) {
            return List.of(String.format(&quot;%s = \&quot;%s\&quot;&quot;, path, obj));
        } else if (isBoxedPrimitive(obj)) {
            return List.of(String.format(&quot;%s = %s&quot;, path, obj));
        } else if (obj.getClass().isArray()) {
            arrayPaths(paths, path, obj);
        } else if (obj instanceof Collection) {
            // TODO extra properties of custom classes implementing Collection
            collectionPaths(paths, path, (Collection&lt;?&gt;) obj);
        } else if (obj instanceof Map) {
            // TODO extra properties of custom classes implementing Map
            mapPaths(paths, path, (Map&lt;?, ?&gt;) obj);
        } else {
            beanPaths(paths, path, obj);
        }

        return paths;
    }

    private static void arrayPaths(final List&lt;String&gt; paths, final String path, final Object obj) {
        if (obj instanceof int[]) {
            final int[] ints = (int[]) obj;
            for (int i = 0; i &lt; ints.length; i++) {
                paths.add(indexed(path, i) + &quot; = &quot; + ints[i]);
            }
        } else if (obj instanceof long[]) {
            final long[] longs = (long[]) obj;
            for (int i = 0; i &lt; longs.length; i++) {
                paths.add(indexed(path, i) + &quot; = &quot; + longs[i]);
            }
        } else if (obj instanceof boolean[]) {
            final boolean[] booleans = (boolean[]) obj;
            for (int i = 0; i &lt; booleans.length; i++) {
                paths.add(indexed(path, i) + &quot; = &quot; + booleans[i]);
            }
        } else if (obj instanceof float[]) {
            final float[] floats = (float[]) obj;
            for (int i = 0; i &lt; floats.length; i++) {
                paths.add(indexed(path, i) + &quot; = &quot; + floats[i]);
            }
        } else if (obj instanceof double[]) {
            final double[] doubles = (double[]) obj;
            for (int i = 0; i &lt; doubles.length; i++) {
                paths.add(indexed(path, i) + &quot; = &quot; + doubles[i]);
            }
        } else {
            final Object[] objects = (Object[]) obj;
            for (int i = 0; i &lt; objects.length; i++) {
                paths.addAll(paths(indexed(path, i), objects[i]));
            }
        }
    }

    private static void collectionPaths(final List&lt;String&gt; paths, final String path, final Collection&lt;?&gt; obj) {
        int idx = 0;
        for (final Object current : obj) {
            paths.addAll(paths(indexed(path, idx), current));
            ++idx;
        }
    }

    private static void mapPaths(final List&lt;String&gt; paths, final String path, final Map&lt;?, ?&gt; obj) {
        for (final Map.Entry&lt;?, ?&gt; entry : obj.entrySet()) {
            paths.addAll(paths(keyed(path, entry.getKey()), entry.getValue()));
        }
    }

    private static void beanPaths(final List&lt;String&gt; paths, final String path, final Object obj) {
        try {
            final BeanInfo beanInfo = Introspector.getBeanInfo(obj.getClass(), Object.class);
            for (final PropertyDescriptor pd : beanInfo.getPropertyDescriptors()) {
                final Method readMethod = pd.getReadMethod();
                if (readMethod != null) {
                    final Object value = readMethod.invoke(obj);
                    paths.addAll(paths(nested(path, pd.getName()), value));
                }
            }
        } catch (final IntrospectionException | InvocationTargetException | IllegalAccessException ex) {
            throw new RuntimeException(ex);
        }
    }

    private static boolean isBoxedPrimitive(final Object obj) {
        return obj instanceof Boolean
                || obj instanceof Integer
                || obj instanceof Long
                || obj instanceof Float
                || obj instanceof Double;
    }

    private static String nested(final String path, final String propName) {
        return path.isEmpty()
                ? propName
                : path + &#39;.&#39; + propName;
    }

    private static String keyed(final String path, final Object key) {
        return path + &quot;[\&quot;&quot; + key + &quot;\&quot;]&quot;;
    }

    private static String indexed(final String path, final int idx) {
        return path + &#39;[&#39; + idx + &#39;]&#39;;
    }

}
