    	public &lt;T&gt; T getProperty(String key) {
    		return (T)properties.get(key);
    	}
    
    	public Map&lt;String, Object&gt; getProperties() {
    		return properties;
    	}
    
    	public void setProperties(Map&lt;String, Object&gt; properties) {
